package com.amadeus.android.domain.air.apis

import com.amadeus.android.ApiResponse
import com.amadeus.android.domain.resources.FlightDate
import retrofit2.http.GET

@JvmSuppressWildcards
interface FlightDatesApi {
    /**
     * Find the cheapest flight dates from an origin to a destination.
     * Find your future travel dates.  ### Query example When is the cheapest date to fly to Munich from Madrid? <pre><code> GET https://test.api.amadeus.com/v1/shopping/flight-dates?origin=MAD&destination=MUC </code></pre>  ### Response example <pre><code> {     \"meta\": {         \"links\": {             \"self\": \"https://test.api.amadeus.com/v1/shopping/flight-dates?origin=MAD&destination=MUC&departureDate=2020-02-28,2020-03-17&oneWay=false&duration=1,15&nonStop=false&viewBy=DURATION\"         },         \"currency\": \"USD\",         \"defaults\": {             \"nonStop\": false,             \"departureDate\": \"2020-02-28,2020-03-17\",             \"oneWay\": false,             \"duration\": \"1,15\",             \"viewBy\": \"DURATION\"         }     },     \"data\": [         {             \"type\": \"flight-date\",             \"origin\": \"MAD\",             \"destination\": \"MUC\",             \"departureDate\": \"2020-02-28\",             \"returnDate\": \"2020-03-01\",             \"price\": {                 \"total\": \"120.58\"             },             \"links\": {                 \"flightDestinations\": \"https://test.api.amadeus.com/v1/shopping/flight-destinations?origin=MAD&departureDate=2020-02-28,2020-03-17&oneWay=false&duration=1,15&nonStop=false&viewBy=DURATION\",                 \"flightOffers\": \"https://test.api.amadeus.com/v2/shopping/flight-offers?originLocationCode=MAD&destinationLocationCode=MUC&departureDate=2020-02-28&returnDate=2020-03-01&adults=1&nonStop=false\"             }         },         {             \"type\": \"flight-date\",             \"origin\": \"MAD\",             \"destination\": \"MUC\",             \"departureDate\": \"2020-02-28\",             \"returnDate\": \"2020-03-07\",             \"price\": {                 \"total\": \"120.58\"             },             \"links\": {                 \"flightDestinations\": \"https://test.api.amadeus.com/v1/shopping/flight-destinations?origin=MAD&departureDate=2020-02-28,2020-03-17&oneWay=false&duration=1,15&nonStop=false&viewBy=DURATION\",                 \"flightOffers\": \"https://test.api.amadeus.com/v2/shopping/flight-offers?originLocationCode=MAD&destinationLocationCode=MUC&departureDate=2020-02-28&returnDate=2020-03-07&adults=1&nonStop=false\"             }         },         {             \"type\": \"flight-date\",             \"origin\": \"MAD\",             \"destination\": \"MUC\",             \"departureDate\": \"2020-02-28\",             \"returnDate\": \"2020-03-10\",             \"price\": {                 \"total\": \"120.58\"             },             \"links\": {                 \"flightDestinations\": \"https://test.api.amadeus.com/v1/shopping/flight-destinations?origin=MAD&departureDate=2020-02-28,2020-03-17&oneWay=false&duration=1,15&nonStop=false&viewBy=DURATION\",                 \"flightOffers\": \"https://test.api.amadeus.com/v2/shopping/flight-offers?originLocationCode=MAD&destinationLocationCode=MUC&departureDate=2020-02-28&returnDate=2020-03-10&adults=1&nonStop=false\"             }         },         {             \"type\": \"flight-date\",             \"origin\": \"MAD\",             \"destination\": \"MUC\",             \"departureDate\": \"2020-02-28\",             \"returnDate\": \"2020-03-12\",             \"price\": {                 \"total\": \"120.58\"             },             \"links\": {                 \"flightDestinations\": \"https://test.api.amadeus.com/v1/shopping/flight-destinations?origin=MAD&departureDate=2020-02-28,2020-03-17&oneWay=false&duration=1,15&nonStop=false&viewBy=DURATION\",                 \"flightOffers\": \"https://test.api.amadeus.com/v2/shopping/flight-offers?originLocationCode=MAD&destinationLocationCode=MUC&departureDate=2020-02-28&returnDate=2020-03-12&adults=1&nonStop=false\"             }         },         {             \"type\": \"flight-date\",             \"origin\": \"MAD\",             \"destination\": \"MUC\",             \"departureDate\": \"2020-02-28\",             \"returnDate\": \"2020-03-13\",             \"price\": {                 \"total\": \"120.58\"             },             \"links\": {                 \"flightDestinations\": \"https://test.api.amadeus.com/v1/shopping/flight-destinations?origin=MAD&departureDate=2020-02-28,2020-03-17&oneWay=false&duration=1,15&nonStop=false&viewBy=DURATION\",                 \"flightOffers\": \"https://test.api.amadeus.com/v2/shopping/flight-offers?originLocationCode=MAD&destinationLocationCode=MUC&departureDate=2020-02-28&returnDate=2020-03-13&adults=1&nonStop=false\"             }         }     ],     \"dictionaries\": {         \"locations\": {             \"MAD\": {                 \"subType\": \"AIRPORT\",                 \"detailedName\": \"ADOLFO SUAREZ BARAJAS\"             },             \"MUC\": {                 \"subType\": \"AIRPORT\",                 \"detailedName\": \"MUNICH INTERNATIONAL\"             }         },         \"currencies\": {             \"EUR\": \"EURO\"         }     } } </code></pre> 
     * The endpoint is owned by defaultname service owner
     * @param origin IATA code of the city from which the flight will depart  [IATA table codes](http://www.iata.org/publications/Pages/code-search.aspx) - e.g. MAD for Madrid  (required)
     * @param destination IATA code of the city to which the flight is going.  [IATA table codes](http://www.iata.org/publications/Pages/code-search.aspx) - e.g. MUC for Munich  (required)
     * @param departureDate the date, or range of dates, on which the flight will depart from the origin. Dates are specified in the [ISO 8601](https://en.wikipedia.org/wiki/ISO_8601) YYYY-MM-DD format, e.g. 2017-12-25. Ranges are specified with a comma and are inclusive (optional)
     * @param oneWay if this parameter is set to true, only one-way flights are considered. If this parameter is not set or set to false, only round-trip flights are considered (optional, default to false)
     * @param duration exact duration or range of durations of the travel, in days. This parameter must not be set if oneWay is true. Ranges are specified with a comma and are inclusive, e.g. 2,8 (optional)
     * @param nonStop if this parameter is set to true, only flights going from the origin to the destination with no stop in-between are considered (optional, default to false)
     * @param maxPrice defines the price limit for each offer returned. The value should be a positive number, without decimals (optional)
     * @param viewBy view the flight dates by DATE, DURATION, or WEEK. View by DATE (default when oneWay is true) to get the cheapest flight dates for every departure date in the given range. View by DURATION (default when oneWay is false) to get the cheapest flight dates for every departure date and for every duration in the given ranges. View by WEEK to get the cheapest flight destination for every week in the given range of departure dates. Note that specifying a detailed view but large ranges may result in a huge number of flight dates being returned. For some very large numbers of flight dates, the API may refuse to provide a response (optional)
     */
    @GET("shopping/flight-dates")
    suspend fun getFlightDates(
        @retrofit2.http.Query("origin") origin: String,
        @retrofit2.http.Query("destination") destination: String,
        @retrofit2.http.Query("departureDate") departureDate: String?,
        @retrofit2.http.Query("oneWay") oneWay: Boolean?,
        @retrofit2.http.Query("duration") duration: String?,
        @retrofit2.http.Query("nonStop") nonStop: Boolean?,
        @retrofit2.http.Query("maxPrice") maxPrice: Long?,
        @retrofit2.http.Query("viewBy") viewBy: String?
    ): ApiResponse<List<FlightDate>>
}
